mapper.select:
select user_name, user_password, user_email, user_profile_image_url, user_register_date, interest_code, grade_code
  from users
 where user_email = #{userEmail}

select eventKey, userId, attendDateTime
  from event_attend
 where user_email = #{userEmail}

select token
  from users
 where user_email = #{userEmail}

